create schema videoclub;

set schema 'videoclub';

create table telefono(
	id smallserial primary key,
	valor varchar(15) not null,
	dni varchar(10) not null
);

create table persona(
	dni varchar(10) primary key,
	fecha_nacimiento date not null,
	nombre varchar(30) not null,
	apellidos varchar(80) not null
);

create table direccion(
	dni varchar(10) primary key,
	codigo_postal varchar(5) null,
	calle varchar(80) null,
	numero varchar(5) null,
	piso varchar(10) null
);

create table socio(
	id smallserial primary key,
	dni varchar(10) not null
);

create table pelicula(
	id int4 primary key,
	titulo varchar(80) not null,
	genero varchar(50) not null,
	sinopsis text not null,
	director varchar(80) not null
);

create table prestamo(
	id smallserial primary key,
	id_socio smallint not null,
	id_pelicula smallint not null,
	fecha_alquiler date null,
	fecha_devolucion date null
);

alter table telefono 
add constraint fk_persona_telefono 
foreign key (dni) references persona(dni);

alter table direccion
add constraint fk_persona_direccion 
foreign key (dni) references persona(dni);

alter table socio
add constraint fk_persona_socio 
foreign key (dni) references persona(dni);

alter table prestamo
add constraint fk_socio_prestamo 
foreign key (id_socio) references socio(id);

alter table prestamo
add constraint fk_pelicula_prestamo 
foreign key (id_pelicula) references pelicula(id);

CREATE TABLE tmp_videoclub (
	id_copia int4 NULL,
	fecha_alquiler_texto date NULL,
	dni varchar(50) NULL,
	nombre varchar(50) NULL,
	apellido_1 varchar(50) NULL,
	apellido_2 varchar(50) NULL,
	email varchar(50) NULL,
	telefono varchar(50) NULL,
	codigo_postal varchar(5) NULL,
	fecha_nacimiento varchar(50) NULL,
	numero varchar(5) NULL,
	piso varchar(5) NULL,
	letra varchar NULL,
	calle varchar NULL,
	ext varchar(50) NULL,
	titulo varchar(80) NULL,
	genero varchar(50) NULL,
	sinopsis text NULL,
	director varchar(80) NULL,
	fecha_alquiler date NULL,
	fecha_devolucion date NULL
);
	 
select distinct tv.dni, cast(fecha_nacimiento as date), nombre, concat(apellido_1, ' ', apellido_2)  from tmp_videoclub tv;

select distinct tv.telefono, tv.dni from tmp_videoclub tv;

select distinct tv.dni, tv.codigo_postal, concat(calle, ' ', ext), tv.numero, concat(piso,' ', letra)  from tmp_videoclub tv;

select distinct tv.dni from tmp_videoclub tv;

select distinct tv.id_copia, tv.titulo, tv.genero, tv.director, tv.sinopsis from tmp_videoclub tv;

select s.id, p.id, cast(fecha_alquiler as date), cast(fecha_devolucion as date) from tmp_videoclub tv
join socio s on tv.dni = s.dni
join pelicula p on tv.id_copia = p.id;


select p.titulo, count(p.id) as copias_disponibles
from pelicula p
left join prestamo pr on p.id = pr.id_pelicula
where pr.fecha_devolucion is not null
group by p.titulo;

